import React from "react";
import PropTypes from "prop-types";

// react plugin for creating charts
import ChartistGraph from "react-chartist";
// @material-ui/core
import withStyles from "@material-ui/core/styles/withStyles";
import Icon from "@material-ui/core/Icon";
// @material-ui/icons
import Warning from "@material-ui/icons/Warning";
import Edit from "@material-ui/icons/Edit";
import Close from "@material-ui/icons/Close";
import Check from "@material-ui/icons/Check";
import Add from "@material-ui/icons/Add";
import Modal from '@material-ui/core/Modal';
import ArrowUpward from "@material-ui/icons/ArrowUpward";
import AccessTime from "@material-ui/icons/AccessTime";
// core components
import CustomInput from "components/CustomInput/CustomInput.jsx";
import Datetime from "react-datetime";
import FormControl from "@material-ui/core/FormControl";
import GridItem from "components/Grid/GridItem.jsx";
import GridContainer from "components/Grid/GridContainer.jsx";
import Table from "components/Table/Table.jsx";
import CustomTabs from "components/CustomTabs/CustomTabs.jsx";
import Danger from "components/Typography/Danger.jsx";
import Card from "components/Card/Card.jsx";
import CardHeader from "components/Card/CardHeader.jsx";
import CardIcon from "components/Card/CardIcon.jsx";
import CardBody from "components/Card/CardBody.jsx";
import CardFooter from "components/Card/CardFooter.jsx";
import Button from "components/CustomButtons/Button.jsx";
import API from "../../adapters/API";
import InnerModal from './InnerModal'
import {GetMonthlyLabels} from '../../variables/Labels'
import {sortDatesHighToLow, returnSeries} from '../../variables/DateSort'



import dashboardStyle from "assets/jss/material-dashboard-pro-react/views/dashboardStyle.jsx";

function getModalStyle() {
  return {
    position: 'absolute',
    // top: `${50}%`,
    left: 'auto',
    width: 400 + 'px',
    padding: 200 + 'px',
    // transform: `translate(-${50}%, -${45}%)`,
  };
}


// CHARTS

var Chartist = require("chartist");

var delays = 80,
  durations = 500;

const MonthlyBillChart = (that) => ({
  data: {
    labels: GetMonthlyLabels(),
    series: returnSeries(that.props.utilityData.bills.sort(sortDatesHighToLow))
  },
  options: {
    lineSmooth: Chartist.Interpolation.cardinal({
      tension: 0
    }),
    low: 0,
    high: that.maxValue(), // creative tim: we recommend you to set the high sa the biggest value + something for a better look
    chartPadding: {
      top: 0,
      right: 0,
      bottom: 0,
      left: 0
    }
  },
  // for animation
  animation: {
    draw: function(data) {
      if (data.type === "line" || data.type === "area") {
        data.element.animate({
          d: {
            begin: 600,
            dur: 700,
            from: data.path
              .clone()
              .scale(1, 0)
              .translate(0, data.chartRect.height())
              .stringify(),
            to: data.path.clone().stringify(),
            easing: Chartist.Svg.Easing.easeOutQuint
          }
        });
      } else if (data.type === "point") {
        data.element.animate({
          opacity: {
            begin: (data.index + 1) * delays,
            dur: durations,
            from: 0,
            to: 1,
            easing: "ease"
          }
        });
      }
    }
  }
});



class ShowUtilityCard extends React.Component {


  state = {
    value: 0,
    billDate: "",
    cost: "",
    open: false,
    billData: {}
  };


maxValue = () => {
  let max = Math.max(returnSeries(this.props.utilityData.bills.sort(sortDatesHighToLow)))
  return max+(max*0.2)
}


  sortedBills = () => this.props.utilityData.bills.sort(sortDatesHighToLow)

  handleOpen = () => {
    this.setState({ open: true });
  };

  handleClose = () => {
    this.setState({ open: false });
  };


