import React from "react";
import PropTypes from "prop-types";
// react plugin for creating charts
import ChartistGraph from "react-chartist";
// @material-ui/core
import withStyles from "@material-ui/core/styles/withStyles";
import Icon from "@material-ui/core/Icon";
import CardIcon from "components/Card/CardIcon.jsx";
// @material-ui/icons
// import Store from "@material-ui/icons/Store";
import Warning from "@material-ui/icons/Warning";
// import DateRange from "@material-ui/icons/DateRange";
// import LocalOffer from "@material-ui/icons/LocalOffer";
// import Update from "@material-ui/icons/Update";
// import ArrowUpward from "@material-ui/icons/ArrowUpward";
import AccessTime from "@material-ui/icons/AccessTime";
// import Accessibility from "@material-ui/icons/Accessibility";
import BugReport from "@material-ui/icons/BugReport";
import Code from "@material-ui/icons/Code";
import Cloud from "@material-ui/icons/Cloud";
// core components
import GridItem from "components/Grid/GridItem.jsx";
import GridContainer from "components/Grid/GridContainer.jsx";
import Table from "components/Table/Table.jsx";
import Tasks from "components/Tasks/Tasks.jsx";
import CustomTabs from "components/CustomTabs/CustomTabs.jsx";
// import Danger from "components/Typography/Danger.jsx";
import Card from "components/Card/Card.jsx";
import CardHeader from "components/Card/CardHeader.jsx";
// import CardIcon from "components/Card/CardIcon.jsx";
import CardBody from "components/Card/CardBody.jsx";
import CardFooter from "components/Card/CardFooter.jsx";
import {GetMonthlyLabels, ThisMonth} from '../../variables/Labels'
import { returnSeries, GetMonthBillCostsAndLabels} from '../../variables/DateSort'
import Timeline from "@material-ui/icons/Timeline";

import { bugs, website, server } from "variables/general.jsx";

import {
  dailySalesChart,
  // emailsSubscriptionChart,
  completedTasksChart
} from "variables/charts.jsx";

import dashboardStyle from "assets/jss/material-dashboard-pro-react/views/dashboardStyle.jsx";


var Chartist = require("chartist");

var delays = 80,
  durations = 500;


const MonthlyBillChart = (that) => ({
  data: {
    labels: GetMonthlyLabels(),
    series: returnSeries(that.allBillsArray())
  },
  options: {
    lineSmooth: Chartist.Interpolation.cardinal({
      tension: 0,
      showArea: true
    }),
    low: 0,
    high: 90, // creative tim: we recommend you to set the high sa the biggest value + something for a better look
    chartPadding: {
      top: 0,
      right: 0,
      bottom: 20,
      left: 0
    }
  },
  // for animation
  animation: {
    draw: function(data) {
      if (data.type === "line" || data.type === "area") {
        data.element.animate({
          d: {
            begin: 600,
            dur: 700,
            from: data.path
              .clone()
              .scale(1, 0)
              .translate(0, data.chartRect.height())
              .stringify(),
            to: data.path.clone().stringify(),
            easing: Chartist.Svg.Easing.easeOutQuint
          }
        });
      } else if (data.type === "point") {
        data.element.animate({
          opacity: {
            begin: (data.index + 1) * delays,
            dur: durations,
            from: 0,
            to: 1,
            easing: "ease"
          }
        });
      }
    }
  }
});

const MonthlyBillBarChart = (that) => ({
  data: {
    labels: that.props.userInfo.utilities && that.getMonthlyBillLabels(),
    series: that.props.userInfo.utilities && that.getMonthlyBillCosts()
  },
  options: {
    // reverseData: true,
    horizontalBars: true,
    axisX: {
      showGrid: false,
      offset: 70
    },
    chartPadding: {
      top: 0,
      right: 5,
      bottom: 0,
      left: 0
    }
  },
});





class Dashboard extends React.Component {
  state = {
    value: 0,
    allBills: {}
  };

allUtilitiesArray = () => {
  let allUtilities = []
    this.props.userInfo.utilities && this.props.userInfo.utilities.map(utility =>  allUtilities = [...allUtilities, utility])
    return allUtilities
}

getMonthlyBillLabels = () => {
  return (GetMonthBillCostsAndLabels(this.allUtilitiesArray()).map(bill => bill.utility_type))
}

getMonthlyBillCosts = () => {
  return [(GetMonthBillCostsAndLabels(this.allUtilitiesArray()).map(bill => bill.cost))]
}

getTotalMonthlyOutgoings = () => {
let totalCost = 0
GetMonthBillCostsAndLabels(this.allUtilitiesArray()).map(bill => totalCost = totalCost + bill.cost)
return parseFloat(Math.round(totalCost * 100) / 100).toFixed(2)

}

allBillsArray = () => {
    let allBills = []
    this.props.userInfo.utilities && this.props.userInfo.utilities.map(utility => utility.bills.map(bill => {
      return allBills = [...allBills, bill]
    }))
    return allBills
  }

  handleChange = (event, value) => {
    this.setState({ value });
  };

  handleChangeIndex = index => {
    this.setState({ value: index });
  };
  render() {
    const { classes } = this.props;
    return (
      <div>

        <GridContainer>
          <GridItem xs={12} sm={12} md={12}>
            <Card chart>
              <CardHeader color="success">
                <ChartistGraph
                  className="ct-chart"
                  data={MonthlyBillChart(this).data}
                  type="Line"
                  options={MonthlyBillChart.options}
                  listener={MonthlyBillChart.animation}
                />
              </CardHeader>
              <CardBody>
                <h4 className={classes.cardTitle}>Yearly outgoings by month</h4>
              </CardBody>
              <CardFooter chart>
                <div className={classes.stats}>
                  <AccessTime /> Hello
                </div>
              </CardFooter>
            </Card>
          </GridItem>
        </GridContainer>  
        <GridContainer>


          <GridItem xs={12} sm={4} md={4} lg={4}>
            <Card>
              <CardHeader color="warning" stats icon>
                <CardIcon color="warning">
                  <Icon>date_range</Icon>
                </CardIcon>
