import React from "react";
import PropTypes from "prop-types";
// react plugin for creating charts
import ChartistGraph from "react-chartist";
// @material-ui/core
import withStyles from "@material-ui/core/styles/withStyles";
import Icon from "@material-ui/core/Icon";
import CardIcon from "components/Card/CardIcon.jsx";
// @material-ui/icons
// import Store from "@material-ui/icons/Store";
import Warning from "@material-ui/icons/Warning";
// import DateRange from "@material-ui/icons/DateRange";
// import LocalOffer from "@material-ui/icons/LocalOffer";
import Update from "@material-ui/icons/Update";
// import ArrowUpward from "@material-ui/icons/ArrowUpward";
import AccessTime from "@material-ui/icons/AccessTime";
// import Accessibility from "@material-ui/icons/Accessibility";
import BugReport from "@material-ui/icons/BugReport";
import Code from "@material-ui/icons/Code";
import Cloud from "@material-ui/icons/Cloud";
// core components
import GridItem from "components/Grid/GridItem.jsx";
import GridContainer from "components/Grid/GridContainer.jsx";
import Table from "components/Table/Table.jsx";
import Tasks from "components/Tasks/Tasks.jsx";
import CustomTabs from "components/CustomTabs/CustomTabs.jsx";
// import Danger from "components/Typography/Danger.jsx";
import Card from "components/Card/Card.jsx";
import CardHeader from "components/Card/CardHeader.jsx";
// import CardIcon from "components/Card/CardIcon.jsx";
import CardBody from "components/Card/CardBody.jsx";
import CardFooter from "components/Card/CardFooter.jsx";
import {GetMonthlyLabels, ThisMonth} from '../../variables/Labels'
import { returnSeries, GetMonthBillCostsAndLabels, GetYearBillCostsAndLabels} from '../../variables/DateSort'
import Timeline from "@material-ui/icons/Timeline";
import chartsStyle from "assets/jss/material-dashboard-pro-react/views/chartsStyle.jsx";
import "assets/css/material-dashboard-pro-react.css";

import { bugs, website, server } from "variables/general.jsx";
import dashboardStyle from "assets/jss/material-dashboard-pro-react/views/dashboardStyle.jsx";


var Chartist = require("chartist");

var delays = 80,
  durations = 500;
  var delays2 = 80,
  durations2 = 500;

  const yearlyPieChart = (that) => ({
    data: {
      labels: that.props.userInfo.utilities && that.getYearlyBillLabels(),
      series: that.props.userInfo.utilities && that.getYearlyBillCosts().flat()
    },
    options: {
      height: "400px",
      labelOffset: 80,
      chartPadding: 60,
      donut: true,
      donutWidth: 60,
      donutSolid: false,
      startAngle: 270,
    }
  });

  const monthlyPieChart = (that) => ({
    data: {
      labels: that.props.userInfo.utilities && that.getMonthlyBillLabels(),
      series: that.props.userInfo.utilities && that.getMonthlyBillCosts().flat()
    },
    options: {
      height: "400px",
      labelOffset: 80,
      chartPadding: 60,
      donut: true,
      donutWidth: 60,
      donutSolid: false,
      startAngle: 270,
    }
  });



const MonthlyBillChart = (that) => ({
  data: {
    labels: GetMonthlyLabels(),
    series: returnSeries(that.allBillsArray())
  },
  options: {
    style: chartsStyle,
    lineSmooth: Chartist.Interpolation.cardinal({
      tension: 10,
    }),
    chartPadding: {
      top: 0,
      right: 0,
      bottom: 20,
      left: 0
    },
    axisY: {
      showGrid: true,
      offset: 40
    },
    axisX: {
      showGrid: false
    },
    low: that.minValue(),
    high: that.maxValue(),
    showPoint: true,
    showArea: true
  },
  // for animation
  animation: {
    draw: function(data) {
      if (data.type === "line" || data.type === "area") {
        data.element.animate({
          d: {
            begin: 600,
            dur: 700,
            from: data.path
              .clone()
              .scale(1, 0)
              .translate(0, data.chartRect.height())
              .stringify(),
            to: data.path.clone().stringify(),
            easing: Chartist.Svg.Easing.easeOutQuint
          }
        });
      } else if (data.type === "point") {
        data.element.animate({
          opacity: {
            begin: (data.index + 1) * delays,
            dur: durations,
            from: 0,
            to: 1,
            easing: "ease"
          }
        });
      }
    }
  }
});

const MonthlyBillBarChart = (that) => ({
  data: {
    labels: that.props.userInfo.utilities && that.getMonthlyBillLabels(),
    series: that.props.userInfo.utilities && that.getMonthlyBillCosts()
  },
  options: {
    // reverseData: true,

    axisX: {
      showGrid: false,
      // offset: 70
    },
    chartPadding: {
      top: 0,
      right: 5,
      bottom: 0,
      left: 0
    }
  },
  responsiveOptions: [
    [
      "screen and (max-width: 640px)",
      {
        seriesBarDistance: 5,
        axisX: {
          labelInterpolationFnc: function(value) {
            return value[0];
          }
        }
      }
    ]
  ],
  animation: {
    draw: function(data) {
      if (data.type === "bar") {
        data.element.animate({
          opacity: {
            begin: (data.index + 1) * delays2,
            dur: durations2,
            from: 0,
            to: 1,
            easing: "ease"
          }
        });
      }
    }
  }

});





class Dashboard extends React.Component {
  state = {
    value: 0,
    allBills: {}
  };

  maxValue = () => {
